mapper.update:
UPDATE BOB
title = #{title}, content = #{content:VARCHAR}, background_image = #{image:VARCHAR},
location_name = #{locationName: VARCHAR}, latitude = #{latitude: LONGVARCHAR}, longitude = #{longitude: LONGVARCHAR}, time = #{appointmentTime:DATE},
fee = #{fee: INTEGER}, fee_date = #{feeDate: DATE}
bob_id = #{bobId}

mapper.select:
SELECT
	participant_id
FROM PARTICIPANT

	user_id = #{userId}
	AND bob_id = #{bobId}

insert.if:
INSERT INTO PARTICIPANT(participant_id, participated_date, status, bob_id, user_id) VALUES (
seq_participant_id.nextval, SYSDATE, '1', #{bobId: INTEGER}, #{item: INTEGER}
