mapper.update:
UPDATE USERS

	
		total_point = total_point + #{point}
	
	
		total_point = total_point - #{point}
	
	
		total_mileage = total_mileage + #{point}
	
	
		total_mileage = total_mileage - #{point}
	


	user_id = #{userId}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT payment_id, to_char(time,'YYYY-MM-DD HH24:MI:SS') AS time, money, payment_code FROM PAYMENT
user_id = #{userId}
ORDER BY time DESC ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT payment_id, to_char(time,'YYYY-MM-DD HH24:MI:SS') AS time, money, payment_code, users.bank_code, users.account_number, users.real_name, payment.money AS point FROM PAYMENT, USERS
PAYMENT.user_id = USERS.user_id AND payment_code IN ('P03', 'P04')
ORDER BY payment_code ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
