mapper.select:
SELECT 	COUNT(*)	
FROM users 
WHERE email = #{value}
AND sns_type = #{value}

SELECT COUNT(*)
FROM users 
WHERE email= #{email}

mapper.update:
UPDATE users
role = #{role:CHAR}, email = #{email}, password = #{password}, address = #{address:VARCHAR}, gender = #{gender:CHAR}, phone = #{phone:VARCHAR}, birth = #{birth:VARCHAR}, profile_image = #{profileImage:VARCHAR}
real_name = #{realName:VARCHAR}, bank_name = #{bankName:VARCHAR}, account_number = #{accountNumber:VARCHAR}
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT user_id, email, name, phone, address total_point, total_mileage, real_name, bank_name, account_number, role FROM users
user_id = #{searchKeyword}
name = #{searchKeyword}
ORDER BY user_id ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

